mapper.select:
SELECT user_id, user_name, provider_user_id, email, role, created_at
FROM users
WHERE user_id = #{userId}

SELECT u.user_id AS userId,
       u.user_name AS userName,
       u.email AS email
FROM users u
JOIN users_classes uc ON u.user_id = uc.user_id
WHERE uc.class_id = #{classId}
AND u.role = 'STUDENT'
ORDER BY u.user_name

    LIMIT #{limit}


    OFFSET #{offset}

        SELECT u.user_id AS userId,
u.user_name AS userName,
u.email AS email
FROM users u


WHERE NOT EXISTS (
    SELECT 1 FROM users_classes uc WHERE uc.user_id = u.user_id AND uc.class_id = #{classId}
)
AND u.role = 'STUDENT'
ORDER BY u.user_name

    LIMIT #{limit}


    OFFSET #{offset}
